charts.saql:
q = load "<datasetId>";
<filters>
q = group q by 'nation';
q = foreach q generate q.'nation' as nation, count(q) as Climbs;
q = order q by 'Climbs' desc;
q = limit q 20;

q = load "<datasetId>";
<filters>
q = group q by 'peakid';
q = foreach q generate q.'peakid' as peakid, min(q.'totdays') as A, percentile_disc(0.25) within group (order by q.'totdays') as B, percentile_disc(0.75) within group (order by q.'totdays') as C, max(q.'totdays') as D;
q = limit q 20;

q = load "<datasetId>";
<filters>
q = group q by 'peakid';
q = foreach q generate q.'peakid' as peakid, min(q.'totdays') as A, percentile_disc(0.25) within group (order by q.'totdays') as B, percentile_disc(0.75) within group (order by q.'totdays') as C, max(q.'totdays') as D;
q = order q by A asc;
q = limit q 20;

q = load "<datasetId>";
<filters>
q = group q by 'peakid';
q = foreach q generate q.'peakid' as peakid, avg(q.'camps') as A;
q = order q by A desc;
q = limit q 20;
